//       onClick={(e) => {
//         e.preventDefault()
//         router.push(href)
//       }}
//     >
//       {children}
//       {router.pathname === href ?
//        <style jsx>
//         {`a {
//              color: red;
//           }`}
//       </style>
//       :
//        <style jsx>
//         {`a {
//              color: black;
//           }`}
//       </style>
//       }
//     </a>
//   )
// }

// const router = useRouter()
// const style = {
//   color: router.pathname === href ? 'black' : 'white',
// }

const ActiveLink = ({children, href}) => {
  const router = useRouter()
  const style = {
    color: router.pathname === href ? 'red' : 'black',
    textDecoration: 'none',
    backgroundColor: 'green'
  }
  let flag = false;

  const handleClick = (e) => {
    // console.log('router.pathname=',router.pathname)
    e.preventDefault()
    router.push('/about')
    flag = true;
  }

  return (
    <a href={href} onClick={handleClick}>
      {children}
    {flag ?
       <style jsx>
        {`a {
             color: red;
          }`}
      </style>
      :
       <style jsx>
        {`a {
             color: black;
          }`}
      </style>
      }
    </a>
  )
}

const Navigation = () => {


  return (
    <nav className={styles.zone, styles.blue}>
      <ul className={styles.nav}>

        <li className={styles.li}>
          <Link href="/">
            <a className={styles.a}>Home</a>
          </Link>
        </li>

        <li className={styles.li}>
          {/*<Link href="/about">*/}
          <ActiveLink href="/about">
            <a className={styles.a}>About</a>
          </ActiveLink>
          {/*</Link>*/}
        </li>

        <li className={styles.li}>
          <Link href="/contact">
            <a className={styles.a}>Contact</a>
          </Link>
        </li>

        <li className={styles.li}>
          <Link href="/services">
            <a className={styles.a}>Services</a>
          </Link>
        </li>

        <li className={styles.li,styles.dropdown}>
          <button disabled className={styles.dropBtn}>Dropdown
            <svg class="svg-triangle" className={styles.caret}>
              <polygon points="2,8 20,8 11,18"/>
            </svg>
          </button>

          <div className={styles.dropList}>
              <ul className={styles.dropContainer}>
                <li className={styles.dropItem}>
                  <Link href="/">
                    <a className={styles.a}>Item1</a>
                  </Link>
                </li>
                <li className={styles.dropItem}>
                  <Link href="/">
                    <a className={styles.a}>Item2</a>
                  </Link>
                </li>
              </ul>
          </div>
        </li>

      </ul>
    </nav>
  )
}
export default Navigation
