import React from 'react';
// import Link from 'next/link';
import { useRouter } from 'next/router'
import styles from '../styles/home.module.css'

const Link = ({ children, href }) => {
  const router = useRouter()
  return (
    <a
      href="#"
      onClick={(e) => {
        e.preventDefault()
        router.push(href)
      }}
    >
      {children}
      <style jsx>{`
        a {
          router.pathname === href ? 'black' : 'white';
        }
      `}</style>
    </a>
  )
}
// const router = useRouter()
// const style = {
//   color: router.pathname === href ? 'black' : 'white',
// }

// const handleClick = (e) => {
//   e.preventDefault()
//   router.push(href)
//   console.log('href=',href)
//   console.log('router.pathname=',router.pathname)
// }


const Navigation = () => {
  return (
    <nav className={styles.zone, styles.blue}>
      <ul className={styles.nav}>

        <li className={styles.li}>
          <Link href="/">
            <a className={styles.a}>Home</a>
          </Link>
        </li>

        <li className={styles.li}>
          <Link href="/about">
            <a className={styles.a}>About</a>
          </Link>
        </li>

        <li className={styles.li}>
          <Link href="/contact">
            <a className={styles.a}>Contact</a>
          </Link>
        </li>

        <li className={styles.li}>
          <Link href="/services">
            <a className={styles.a}>Services</a>
          </Link>
        </li>

        <li className={styles.li,styles.dropdown}>
          <button disabled className={styles.dropBtn}>Dropdown
            <svg class="svg-triangle" className={styles.caret}>
              <polygon points="2,8 20,8 11,18"/>
            </svg>
          </button>

          <div className={styles.dropList}>
              <ul className={styles.dropContainer}>
                <li className={styles.dropItem}>
                  <Link href="/">
                    <a className={styles.a}>Item1</a>
                  </Link>
                </li>
                <li className={styles.dropItem}>
                  <Link href="/">
                    <a className={styles.a}>Item2</a>
                  </Link>
                </li>
              </ul>
          </div>
        </li>

      </ul>
    </nav>
  )
}
export default Navigation
