import Head from 'next/head';
import Link from 'next/link';
import Navigation from '../components/Navigation';
import NavigationFooter from '../components/NavigationFooter';
import ActiveSVG from '../components/ActiveSVG'
import styles from '../styles/home.module.css';
// import StylesContact from '../static/StylesContact';
import React, { useState, useEffect } from 'react';

const Contact = () => {

  let myFormRef;
  let myNameRef;
  let mySubjectRef;
  let myMessageRef;
  let myEmailRef;
  let myHoneypotRef;

  const [name, setName] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [email, setEmail] = useState("");
  const [honeypot, setHoneypot] = useState("");

  const [hoverFlag, setHoverFlag] = useState(false);

    // xhr.onreadystatechange = function() {
    //     if (xhr.readyState === 4 && xhr.status === 200) {
    //       form.reset();
    //       var formElements = form.querySelector(".form-elements")
    //       if (formElements) {
    //         formElements.style.display = "none"; // hide form
    //       }
    //       var thankYouMessage = form.querySelector(".thankyou_message");
    //       if (thankYouMessage) {
    //         thankYouMessage.style.display = "block";
    //       }
    //     }
    // };
    // url encode form data for sending as post data
  useEffect(
    () => {
      myFormRef.reset();
    }
  , [])

  const handleSubmit = async event => {
    event.preventDefault();

    if (honeypot != "") {
      return false;
    }

    const fields = ["name", "subject", "message", "email"]
    const data = {
          name: name,
          subject: subject,
          message: message,
          email: email,

          formDataNameOrder: JSON.stringify(fields),
          formGoogleSheetName: "responses",
          formGoogleSendEmail: ""
    }

    const encodedData = Object.keys(data)
      .map(k => (encodeURIComponent(k) + "=" + encodeURIComponent(data[k])))
      .join('&');

// CORS preflight is not available for POST requests when Content-Type is application/x-www-form-urlencoded
// https://security.stackexchange.com/questions/187311/why-cors-preflight-is-not-available-for-post-requests-when-content-type-is-appli
    const res = fetch (
      'https://script.google.com/macros/s/AKfycbxT1I9HS2PVjAVM6wlGG1bDhROXSDifsm7mI-x0qH9U_rgwSQ0tbWTeAA/exec',
      { 
        method: 'POST',
        headers: {'Content-Type': 'application/x-www-form-urlencoded'
        },
        body:encodedData
      })
      .catch(error => console.log(error))

    myFormRef.reset();
  }

  // const handleHover = () => {
  //   console.log('HOVER Enter! hoverFlag=', hoverFlag)
  //   setHoverFlag()
  //   console.log('HOVER Entered! hoverFlag=', hoverFlag)
  // }

  // const handleLeave = () => {
  //   console.log('HOVER Leave! hoverFlag=', hoverFlag)
  //   setHoverFlag()
  //   console.log('HOVER Left! hoverFlag=', hoverFlag)
  // }

  return(
  <div>
    <Head>
      <title>Contact</title>
      <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      <link rel="icon" type="image/svg+xml" href="/AZWWLogo2Minified.svg"/>
