import Head from 'next/head';
import Link from 'next/link';
import Navigation from '../components/Navigation';
import NavigationFooter from '../components/NavigationFooter';
import styles from '../styles/home.module.css';
import StylesContact from '../static/StylesContact';
import React, { useState, useEffect } from 'react';

const Contact = () => {

  let myFormRef;
  let myNameRef;
  let mySubjectRef;
  let myMessageRef;
  let myEmailRef;
  let myColorRef;
  let myHoneypotRef;

  const [name, setName] = useState("");
  const [subject, setSubject] = useState("");
  const [message, setMessage] = useState("");
  const [email, setEmail] = useState("");
  const [color, setColor] = useState("");
  const [honeypot, setHoneypot] = useState("");

    // xhr.onreadystatechange = function() {
    //     if (xhr.readyState === 4 && xhr.status === 200) {
    //       form.reset();
    //       var formElements = form.querySelector(".form-elements")
    //       if (formElements) {
    //         formElements.style.display = "none"; // hide form
    //       }
    //       var thankYouMessage = form.querySelector(".thankyou_message");
    //       if (thankYouMessage) {
    //         thankYouMessage.style.display = "block";
    //       }
    //     }
    // };
    // url encode form data for sending as post data
  useEffect(
    () => {
      // caching effect of loading values left in form
      // console.log("State name before= ",name)
      setName(myNameRef.value)
      setMessage(mySubjectRef.value)
      setMessage(myMessageRef.value)
      setEmail(myEmailRef.value)
      setColor(myColorRef.value)
      setHoneypot(myHoneypotRef.value)
      // console.log("myNameRef= ", myNameRef.value)
      // console.log("State name after= ",name)

      // or can clear form
      // myFormRef.reset();
    }
  , [])

  const handleSubmit = async event => {
    event.preventDefault();

    if (honeypot != "") {
      return false;
    }

    const fields = ["name", "subject", "message", "email", "color"]
    const data = {
          name: name,
          subject: subject,
          message: message,
          email: email,
          color: color,
          formDataNameOrder: JSON.stringify(fields),
          formGoogleSheetName: "responses",
          formGoogleSendEmail: ""
    }

    const encodedData = Object.keys(data)
      .map(k => (encodeURIComponent(k) + "=" + encodeURIComponent(data[k])))
      .join('&');

// CORS preflight is not available for POST requests when Content-Type is application/x-www-form-urlencoded
// https://security.stackexchange.com/questions/187311/why-cors-preflight-is-not-available-for-post-requests-when-content-type-is-appli
    const res = fetch (
      'https://script.google.com/macros/s/AKfycbxT1I9HS2PVjAVM6wlGG1bDhROXSDifsm7mI-x0qH9U_rgwSQ0tbWTeAA/exec',
      { 
        method: 'POST',
        headers: {'Content-Type': 'application/x-www-form-urlencoded'
        },
        body:encodedData
      })
      // .then(response => response.json())
      // .then(data => console.log(data))
      .catch(error => console.log(error))
      // const result = await res.json()

    // document.getElementById("form").reset();
    myFormRef.reset();
  }

  return(
  <div>
    <Head>
      <title>Contact</title>
      <meta name="viewport" content="initial-scale=1.0, width=device-width" />
      <link rel="icon" type="image/svg+xml" href="/AZWWLogo2Minified.svg"/>
    </Head>

    <header className={styles.header}>
      <div id="navbar">
        <Navigation/>
      </div>
    </header>

    <main>

      <h2 class="content-head is-center">Contact Us!</h2>
      <aside>
           <p>
               We would <em>love</em> to hear from you! </p>
               <p>Please use the <b><em>Contact Form </em></b>
               to send us a message.
           </p>
      </aside>

      <link rel="stylesheet" href="https://unpkg.com/purecss@1.0.0/build/pure-min.css"/>
      <link rel="stylesheet" href="https://maxcdn.bootstrapcdn.com/font-awesome/4.4.0/css/font-awesome.min.css"/>
      {/*<link rel="stylesheet" href="style.css"/>*/}

      <form class="gform pure-form pure-form-stacked" id="form" ref={(el) => myFormRef = el}
      onSubmit={handleSubmit}
      >

        <div class="form-elements">
          <fieldset class="pure-group">
            <label htmlFor="name">Name: </label>
            <input id="name" name="name" placeholder="What your Mom calls you" onChange={(e)=>setName(e.target.value)} ref={(el) => myNameRef = el}/>
          </fieldset>

          <fieldset class="pure-group">
            <label htmlFor="subject">Subject: </label>
            <select name="subject" class="" id="subject"
              onChange={(e)=>setMSubject(e.target.value)}
              ref={(el) => mySubjectRef}>
              <option value=""></option>
              <option value="Question">Question</option>
              <option value="I would like a website.">I would like a website</option>
              <option value="Feedback">Feedback</option>
              <option value="Other">Other</option>
            </select>
          </fieldset>

          <fieldset class="pure-group">
            <label htmlFor="message">Message: </label>
            <textarea id="message" name="message" rows="10"
            placeholder="Tell us what's on your mind..."
            onChange={(e)=>setMessage(e.target.value)}
            ref={(el) => myMessageRef = el}></textarea>
          </fieldset>

          <fieldset class="pure-group">
            <label htmlFor="email"><em>Your</em> Email Address:</label>
            <input id="email" name="email" type="email"
            required placeholder="your.name@email.com"
            onChange={(e)=>setEmail(e.target.value)}
            ref={(el) => myEmailRef = el}/>
          </fieldset>

          <fieldset class="pure-group">
            <label htmlFor="color">Favourite Color: </label>
            <input id="color" name="color" placeholder="green" 
            onChange={(e)=>setColor(e.target.value)}
            ref={(el) => myColorRef = el}/>
          </fieldset>

          <fieldset class="pure-group honeypot-field" style={{display:"none"}}>
            <label htmlFor="honeypot">To help avoid spam, utilize a Honeypot technique with a hidden text field; must be empty to submit the form! Otherwise, we assume the user is a spam bot.</label>
            <input id="honeypot" type="text" name="honeypot"
            onChange={(e)=>setHoneypot(e.target.value)}
            ref={(el) => myHoneypotRef = el}/>
          </fieldset>

          <button class="button-success pure-button button-xlarge" type="submit">
            <i class="fa fa-paper-plane"></i>&nbsp;Send</button>
        </div>

        <div class="thankyou_message" style={{display:"none"}}>
          <h2><em>Thanks</em> For contacting us!
            We will get back to you soon!</h2>
        </div>

      </form>

    </main>

    <div className={styles.divider}></div>
    <NavigationFooter/>

  </div>
  )
 }
export default Contact;
